mapper.select:

  	SELECT
	    bno, title, content, writer, regdate, updateDate
	FROM
	    (
	    SELECT
	        @rownum := @rownum + 1 AS rn, bno, title, content, writer, regdate, updateDate
	    FROM
	        (SELECT @rownum := 0) r,
	        board
	    ) AS numbered
	WHERE
	    rn > (#{pageNum} - 1) * #{amount}
	LIMIT #{amount};
